import React, { Component } from 'react'

import { Segment } from 'semantic-ui-react';


class WildPokemonMap extends Component {
    constructor() {
        super();

        this.state = {
            kantoRand: []
        }
    }

    // componentDidMount() {
    //     this.renderRandomPoke();
    // }

    renderRandomPoke = () => {
        let kantoNums = []
        for (let i = 1; i < 5; i++) {
            kantoNums.push(Math.floor((Math.random() * 151) + 1))
        }

        let newKanto = []
        kantoNums.map(num => 
            newKanto.push(this.props.kanto.find(poke => poke.id === num))
            )
        // debugger

        this.setState({
            kantoRand: [...this.state.kantoRand, newKanto]
        },() => console.log(this.state.kantoRand))
    }



    render() {
        return (
            <Segment id='map'>
                {this.renderRandomPoke()}
            </Segment>       
        )
    }
}

export default WildPokemonMap
